export default(state = [], action) => {
    switch(action.type) {
        case "SET_DECK_BOXES":
            return state
        case "ADD_DECK_BOX":
            return [...state, action.payload]
        case "REMOVE_DECK_BOX":
            return state.filter(item => item !== action.payload)
        default:
            return state
    }
}
